var UIConfirmations = function() {
	var n = function() {
		$(".bs_confirmation_component").on("confirmed.bs.confirmation",
				function() {
			 		return true;
				}), $(".bs_confirmation_component").on("canceled.bs.confirmation",
				function() {
					 return false;
				}) 
	};
	return {
		init : function() {
			n()
		}
	}
}();
jQuery(document).ready(function() {
	UIConfirmations.init()
});