
 var getUrlParameter = function getUrlParameter(sParam) {
    var sPageURL = decodeURIComponent(window.location.search.substring(1)),
        sURLVariables = sPageURL.split('&'),
        sParameterName,
        i;

    for (i = 0; i < sURLVariables.length; i++) {
        sParameterName = sURLVariables[i].split('=');

        if (sParameterName[0] === sParam) {
            return sParameterName[1] === undefined ? true : sParameterName[1];
        }
    }
};

var msg;
var theme; 
switch(getUrlParameter('msg')) {
case "constraint":
    msg = "There is a data attached to this record. Unable to delete"
    theme = "ruby" 	
    break;
case "not-deleted":
	msg = "There is problem deleting this record. Delete failed."
	theme = "ruby" 	
	break;
case "delete-success":
	msg = "New record deleted"
	theme = "lime" 		
	break;	
	
case "success":
	msg = "New record created"
	theme = "lime" 		
	break;
default: 
    msg = ""
    theme = ""	
}

 if (msg) {
	$.notific8(msg, {
		life : 5000,
		heading : 'Component deletion',
		theme : theme,
		sticky : true,
		horizontalEdge : 'bottom',
		verticalEdge : 'right',
		zindex : 11500
	});
}
